import pandas as pd#import tensorflow as tffrom tensorflow.keras.preprocessing import image_dataset_from_directoryfrom tensorflow.keras.preprocessing.image import ImageDataGeneratorfrom tensorflow.keras.models import Sequentialfrom tensorflow.keras.layers import Conv2D, MaxPooling2D, Flatten, Dense, Dropout# %% Step 1# Building the modelseq_model = Sequential()seq_model.add(Conv2D(32, (3,3), activation='relu', input_shape=(500, 500, 3)))# Establishing Train, Validation, and Test Directoriestrain_dir = 'data/train'validation_dir = 'data/valid'test_dir = 'data/test'# Performing data augmentationtrain_datagen = ImageDataGenerator(shear_range=0.2, zoom_range=0.2)validation_datagen = ImageDataGenerator(rescale=1./255, shear_range=0.2, zoom_range=0.2)test_datagen = ImageDataGenerator(shear_range=0.2, zoom_range=0.2)# Creating the train and validation generatorstrain_generator = image_dataset_from_directory(train_dir, image_size=(500, 500), batch_size=32, label_mode='categorical')validation_generator = image_dataset_from_directory(validation_dir, image_size=(500, 500), batch_size=32, label_mode='categorical')# %% Step 2# Convolution 2Dseq_model.add(Conv2D(64, (3, 3), activation='relu'))seq_model.add(Conv2D(64, (3, 3), activation='relu'))# Max Poolingseq_model.add(MaxPooling2D((2, 2)))# Flatten Layersseq_model.add(Flatten())# Dense and Dropout Layersseq_model.add(Dense(10))seq_model.add(Dropout(0.25))